<script>
export default {
  methods: {
    accordionToggle(e) {
        let accordion = e.currentTarget.closest('.faq-item')
        let dropdown = accordion.querySelector('.faq-item_dropdown')
        accordion.classList.toggle('active')
        if (accordion.classList.contains('active')) {
            dropdown.style.maxHeight = `${dropdown.scrollHeight + 30}px`
        } else {
            dropdown.style.maxHeight = "0px"
        }

    },
    selectToggle(e) {
        if (!e.target.classList.contains('select-dropdown__show-more') && !e.target.parentElement.classList.contains('select-dropdown__show-more')) {
            e.currentTarget.closest('.select-primary').classList.toggle('active')
            e.currentTarget.closest('.select-primary').querySelector('.select-dropdown').classList.remove('open-all')
        }
    },
    selectChangeValue(e) {
        e.currentTarget.closest('.select-primary').querySelector('.select-value').innerHTML = e.currentTarget.innerHTML
    },
    selectShowMore(e) {
        e.currentTarget.closest('.select-dropdown').classList.toggle('open-all')
    },
    checkBlocksVisibility() {
        let windowHeight = window.innerHeight;
        let blocks = document.querySelectorAll('.fade');
        let delayItem = window.matchMedia("(max-width: 800px)").matches ? 80 : 150

        blocks.forEach(block => {
            let blockPosition = block.getBoundingClientRect().top;

            if (blockPosition < windowHeight - delayItem) {
                block.style.opacity = "1";
                block.style.transform = "translateY(0)";
            }
        });
    }
  },  
  beforeMount () {
    console.log(window)
    window.addEventListener('scroll', this.checkBlocksVisibility);
    this.checkBlocksVisibility();
  },
  beforeDestroy () {
    window.removeEventListener('scroll', this.checkBlocksVisibility);
    this.checkBlocksVisibility();
  },
}
</script>
